import { ArgentBackendNetworkId } from '../backend';
import { Address } from '../chains/starknet/address';
import { NameResolutionAddress, NameResolutionStarknetId, NameResolutionStarknetIds, ProvidersNames } from './schema';
/**
 * IResolveNameService interface
 */
export interface IResolveNameService {
    /**
     * getAddressFromMultipleStarknetId: retrieve addresses from multiple starknetId
     */
    getAddressFromMultipleStarknetId: (network: ArgentBackendNetworkId, starknetIds: string[], includeAvatar?: boolean) => Promise<NameResolutionStarknetIds | undefined>;
    /**
     * getAddressFromStarknetId: retrieve address from starknetId
     */
    getAddressFromStarknetId: (network: ArgentBackendNetworkId, starknetId: string, includeAvatar?: boolean) => Promise<NameResolutionStarknetId | undefined>;
    /**
     * getStarknetIdFromAddress: retrieve starknetId for addresses
     */
    getStarknetIdFromAddress: (network: ArgentBackendNetworkId, addresses: Address[], includeAvatar?: boolean) => Promise<NameResolutionAddress[] | undefined>;
    getProvidersNames: (network: ArgentBackendNetworkId) => Promise<ProvidersNames | undefined>;
}
